import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Buscador.css';
//importo las actions
import { addMovieFavorite, getMovies } from "../../actions"


export class Buscador extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: ""
    };
  }
  handleChange(event) {
    this.setState({ title: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
    //completo la función handleSubmit
    this.props.getMovies(this.state.title)
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <h1>APP MOVIW</h1>
        <h2>¡Encuentra tu pelicula favorita!</h2>
        <form className="form-container" onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label className="label" htmlFor="title">Pelicula: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul>
         {/* Aqui tienes que escribir tu codigo para mostrar la lista de peliculas --
         voy a mapear mi estado donde están las películas y renderizaré un li con un Link
         que me lleve a su detalle y un botón que agregue a favoritas por cada peli.*/}
         {this.props.movies.map((movie) => {
           return (
             <li key={movie.imdbID}>
               <Link to={`/movie/${movie.imdbID}`}>{movie.Title}</Link>
               <button onClick={()=>this.props.addMovieFavorite({title: movie.Title, id: movie.imdbID})}>
                 X
               </button> 
             </li>
           )
         })}
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    movies: state.moviesLoaded
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addMovieFavorite: movie => dispatch(addMovieFavorite(movie)),
    getMovies: title => dispatch(getMovies(title))
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Buscador);
